    public String getChannelGoodsUrl() {
        if (StringUtil.isNull(imgUrl)) {
        	return "";
        }
        int index = imgUrl.lastIndexOf(".");
        String path = "";
        if (index != -1) {
            String prefixPath = imgUrl.substring(0, index);
            String suffixPath = imgUrl.substring(index, imgUrl.length());
            path = Constants.PICTURE_DOMAIN + prefixPath + "--w_70_h_70" +suffixPath;
          //  System.out.println(channelCode);
       
        }
        return path;
    }
    
